  // Update the SVG container.
  function updateSVG() {
    svgWrapper.innerHTML = svgArray[svgStage];
  }

  // Increment svgStage and update the SVG.
  function incrementSvgStage() {
    svgStage = (svgStage + 1) % svgArray.length;
    updateSVG();
  }

  // Set the theme switcher icon based on the current theme.
  function updateThemeIcon() {
    themeSwitcher.innerHTML = isDark ? sunSVG : moonSVG;
  }

  // Toggle dark mode.
  themeSwitcher.addEventListener("click", function () {
    isDark = !isDark;
    document.body.classList.toggle("dark-mode", isDark);
    updateThemeIcon();
    // If default SVG is active (it would update colors if dynamic)
    if (svgStage === 0) {
      updateSVG();
    }
  });

  // Increment the SVG on any keystroke in email or password.
  document.getElementById("email").addEventListener("input", incrementSvgStage);
  document.getElementById("password").addEventListener("input", incrementPasswordSVGStage);
  
  const passwordSVGArray = [getFirstPasswordSVG(), getSecondPasswordSVG(), getThirdPasswordSVG(), getFourthPasswordSVG(), getFifthPasswordSVG(), getFourthPasswordSVG(), getThirdPasswordSVG(), getSecondPasswordSVG()];

function updatePasswordSVG() {
  svgWrapper.innerHTML = passwordSVGArray[passwordSVGStage];
}

function incrementPasswordSVGStage() {
  passwordSVGStage = (passwordSVGStage + 1) % passwordSVGArray.length;
  updatePasswordSVG();
}

/*** Switch back to Email SVG when email field gains focus *****/
document.getElementById("email").addEventListener("focus", function () {
  // Revert SVG container to email field's SVG animation
  updateSVG();
});
  
  
document.getElementById("password").addEventListener("focus", function () {
  passwordSVGStage = 0; // Show first password SVG on click (focus)
  updatePasswordSVG();
});


  // Handle form submission.
  document.getElementById("loginForm").addEventListener("submit", function (e) {
    e.preventDefault();
    console.log("Simulating successful login for:", document.getElementById("email").value);
    showToast("Login successful (Simulated). Redirecting...");
    setTimeout(function () {
      window.location.href = "date-time-setter.html";
    }, 1000);
  });

  // Simple toast function.
  function showToast(message) {
    const toastElem = document.getElementById("toast");
    toastElem.textContent = message;
    toastElem.classList.add("show");
    setTimeout(function () {
      toastElem.classList.remove("show");
    }, 5000);
  }

  // Initial SVG render.
  updateSVG();
  // *** Added initial call to update the theme icon so the sun SVG is visible on load ***
  updateThemeIcon();
